        <div id="debugger"></div>
        <script>
        $("#practiceForm").submit(function(e){
          e.preventDefault();

          let type = $('#typeModal').val();
          let amount = $('#amountModal').val();
          let _token = $("input[name=_token").val();


          $.ajax({
              url: "{{route('practice.add')}}",
              type: "POST",
              data:{
                  type:type,
                  amount:amount,
                  _token:_token
              },
              success: function(response)
              {
                  if(response){
                      window.location.href = "/store-ajax";
                    }
              }
            })

        })
        </script>
